import style from './Card.module.css'
import { Link } from 'react-router-dom'
import { deleteFav, addFav } from '../../redux/actions/actions';
import { useDispatch, useSelector } from 'react-redux';
import { useState } from 'react';


function Card(props) {

   const {name, species, gender, onClose, id} = props

   const dispatch = useDispatch()
   const myFavorites = useSelector(state => state.myFavorites)

   const [ isFav, setIsFav ] = useState(false)

   const handleFavorite = () =>{
      if(isFav === true){
         setIsFav(false)
         dispatch(deleteFav(id))
      }else{
         setIsFav(true)
         dispatch(addFav(name,species,gender,onClose,id))
      }
   }

   return (
      <div className={style.container}>
         <button className={style.btn} onClick={onClose}>X</button>
            {
               isFav ? (
                  <button onClick={handleFavorite}>‚ù§Ô∏è</button>
               ) : (
                  <button onClick={handleFavorite}>ü§ç</button>
               )
            }
         <Link to={`/detail/${id}`}>
            {
               (name.length > 22)
               ? <h2 className={style.titulo_medio}>{name}</h2>
               : <h2 className={style.titulo}>{name}</h2>
            }
            
         </Link>
         <img className={style.image} src={props.image} alt={name} />
         <div className={style.description}>
            <h2>{species}</h2>
            <h2>{gender}</h2>
         </div>
      </div>
   );
}

export default (Card)
