const validation = (data) =>{
    const errors = {}
    
    (!/\S+@\S+\.\S+/.test(data.username)) && (errors.username = 'El email es invalido')
    (!data.username)&&(errors.username='No puede estar vacio')

    return errors;

}

export default validation;
